import React, { useEffect, useRef } from "react";
import { useDispatch, useSelector } from "react-redux";
import { motion } from "framer-motion";
import "bootstrap/dist/css/bootstrap.min.css";
import "./App.css";
import {
  setGameState, setCountdown, setTimer, setScore, setCurrentChar,
  setCharColor, setPosition, setCharSpeed, setDifficulty, resetGame
} from "./redux/gameSlice";

const characters = "ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789!@#$%^&*()";

const App = () => {
  const {
    gameState, countdown, timer, score,
    currentChar, charColor, position, charSpeed, difficulty
  } = useSelector((state) => state.game);
  const dispatch = useDispatch();
  const gameRef = useRef(null);

  useEffect(() => {
    if (gameState === "countdown") {
      let count = 3;
      dispatch(setCountdown(count));
      const interval = setInterval(() => {
        count--;
        dispatch(setCountdown(count));
        if (count === 0) {
          clearInterval(interval);
          dispatch(setGameState("playing"));
          startGame();
        }
      }, 1000);
    }
  }, [gameState, dispatch]);

  useEffect(() => {
    if (gameState === "playing") {
      const interval = setInterval(() => {
        dispatch(setTimer((prev) => {
          if (prev <= 1) {
            clearInterval(interval);
            dispatch(setGameState("gameover"));
            return 0;
          }
          return prev - 1;
        }));
      }, 1000);
      return () => clearInterval(interval);
    }
  }, [gameState, dispatch]);

  useEffect(() => {
    if (gameState === "playing") {
      gameRef.current.focus();
    }
  }, [gameState]);

  const startGame = () => {
    dispatch(resetGame());
    spawnCharacter();
  };

  const spawnCharacter = () => {
    const char = characters[Math.floor(Math.random() * characters.length)];
    const top = `${Math.floor(Math.random() * 60) + 30}%`;
    const left = `${Math.floor(Math.random() * 80)}%`;
    dispatch(setCurrentChar(char));
    dispatch(setPosition({ top, left }));
    dispatch(setCharColor("white"));
    setTimeout(spawnCharacter, charSpeed);
    dispatch(setDifficulty(difficulty + 1));
    dispatch(setCharSpeed(Math.max(charSpeed - difficulty * 80, 8000)));
  };

  const handleKeyPress = (e) => {
    if (gameState !== "playing") return;
    if (e.key === currentChar) {
      dispatch(setScore(score + 5));
      dispatch(setTimer(30));
      dispatch(setCharColor("green"));
      setTimeout(spawnCharacter, 200);
    } else {
      dispatch(setCharColor("red"));
    }
  };

  return (
    <div className="game-container" ref={gameRef} onKeyDown={handleKeyPress} tabIndex="0">
      {gameState === "home" && (
        <div className="home-screen text-center">
          <h1 className="title">Reflex Typing</h1>
          <p className="description">Test your reflexes and typing speed!</p>
          <button className="btn btn-outline-light play-btn" onClick={() => dispatch(setGameState("countdown"))}>
            Play
          </button>
        </div>
      )}

      {gameState === "countdown" && (
        <motion.h1
          className="countdown"
          key={countdown}
          initial={{ scale: 0 }}
          animate={{ scale: 1 }}
          transition={{ duration: 0.4 }}
        >
          {countdown === 0 ? "Start!" : countdown}
        </motion.h1>
      )}

      {gameState === "playing" && (
        <>
          <div className="progress bg-danger" style={{ height: "20px", width: "100%", position: "absolute", top: 0 }}>
            <div className="progress-bar bg-warning" style={{ width: `${(timer / 30) * 100}%` }}></div>
          </div>
          <h2 className="score" style={{ fontSize: "3rem", fontWeight: "bold" }}>
            Score: {score}
          </h2>
          <motion.h1
            className="char-display"
            style={{ ...position, color: charColor }}
            initial={{ scale: 0 }}
            animate={{ scale: 1 }}
            transition={{ duration: 0.3 }}
          >
            {currentChar}
          </motion.h1>
        </>
      )}

      {gameState === "gameover" && (
        <div className="gameover-screen">
          <h1>Game Over!</h1>
          <h4>Final Score: {score}</h4>
          <button
            className="btn btn-success m-3"
            style={{ width: "200px", fontSize: "1.5rem" }}
            onClick={() => dispatch(setGameState("countdown"))}
          >
            Play Again
          </button>
          <button
            className="btn btn-danger m-3"
            style={{ width: "200px", fontSize: "1.5rem" }}
            onClick={() => dispatch(setGameState("home"))}
          >
            Home
          </button>
        </div>
      )}
    </div>
  );
};

export default App;
