import React, { useEffect, useRef } from "react";
import { useDispatch, useSelector } from "react-redux";
import { motion } from "framer-motion";
import {
  setGameState, setCountdown, setTimer, setScore,
  setCurrentChar, setCharColor, setPosition, setCharSpeed,
  setDifficulty, resetGame
} from "./redux/gameSlice";

const characters = "ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789!@#$%^&*()";

const App = () => {
  const {
    gameState, countdown, timer, score,
    currentChar, charColor, position, charSpeed, difficulty
  } = useSelector((state) => state.game);
  const dispatch = useDispatch();
  const gameRef = useRef(null);

  useEffect(() => {
    if (gameState === "countdown") {
      let count = 3;
      dispatch(setCountdown(count));
      const interval = setInterval(() => {
        count--;
        dispatch(setCountdown(count));
        if (count === 0) {
          clearInterval(interval);
          dispatch(setGameState("playing"));
          startGame();
        }
      }, 1000);
    }
  }, [gameState]);

  useEffect(() => {
    if (gameState === "playing") {
      const interval = setInterval(() => {
        dispatch(setTimer((prev) => {
          if (prev <= 1) {
            clearInterval(interval);
            dispatch(setGameState("gameover"));
            return 0;
          }
          return prev - 1;
        }));
      }, 1000);
      return () => clearInterval(interval);
    }
  }, [gameState]);

  useEffect(() => {
    if (gameState === "playing") {
      gameRef.current.focus();
    }
  }, [gameState]);

  const startGame = () => {
    dispatch(resetGame());
    spawnCharacter();
  };

  const spawnCharacter = () => {
    const char = characters[Math.floor(Math.random() * characters.length)];
    const top = `${Math.floor(Math.random() * 60) + 30}%`;
    const left = `${Math.floor(Math.random() * 80)}%`;
    dispatch(setCurrentChar(char));
    dispatch(setPosition({ top, left }));
    dispatch(setCharColor("white"));
    setTimeout(spawnCharacter, charSpeed);
    dispatch(setDifficulty(difficulty + 1));
    dispatch(setCharSpeed(Math.max(charSpeed - difficulty * 80, 8000)));
  };

  const handleKeyPress = (e) => {
    if (gameState !== "playing") return;
    if (e.key === currentChar) {
      dispatch(setScore(score + 5));
      dispatch(setTimer(30));
      dispatch(setCharColor("green"));
      setTimeout(spawnCharacter, 200);
    } else {
      dispatch(setCharColor("red"));
    }
  };

  return (
    <div className="container-fluid vh-100 d-flex flex-column align-items-center justify-content-center bg-dark text-white" ref={gameRef} tabIndex="0" onKeyDown={handleKeyPress}>
      {gameState === "home" && (
        <div className="text-center">
          <h1 className="display-1 fw-bold">Reflex Typing</h1>
          <p className="lead">Test your reflexes and typing speed!</p>
          <button className="btn btn-success btn-lg px-5 py-3 mt-3" onClick={() => dispatch(setGameState("countdown"))}>
            Play
          </button>
        </div>
      )}

      {gameState === "countdown" && (
        <motion.h1
          className="display-1"
          key={countdown}
          initial={{ scale: 0 }}
          animate={{ scale: 1 }}
          exit={{ scale: 0 }}
          transition={{ duration: 0.5 }}
        >
          {countdown === 0 ? "Start!" : countdown}
        </motion.h1>
      )}

      {gameState === "playing" && (
        <>
          <div className="progress w-100" style={{ height: "20px", position: "absolute", top: 0 }}>
            <div
              className="progress-bar bg-warning"
              role="progressbar"
              style={{ width: `${(timer / 30) * 100}%` }}
            ></div>
          </div>
          <h2 className="mt-4 fw-bold">Score: {score}</h2>
          <motion.h1
            className="position-absolute display-1 fw-bold"
            style={{ top: position.top, left: position.left, color: charColor }}
            initial={{ scale: 0 }}
            animate={{ scale: 1 }}
            transition={{ duration: 0.3 }}
          >
            {currentChar}
          </motion.h1>
        </>
      )}

      {gameState === "gameover" && (
        <div className="text-center border rounded p-5 bg-secondary bg-opacity-75">
          <h1 className="display-4">Game Over!</h1>
          <h3>Final Score: {score}</h3>
          <button className="btn btn-primary m-3 btn-lg" onClick={() => dispatch(setGameState("countdown"))}>
            Play Again
          </button>
          <button className="btn btn-danger m-3 btn-lg" onClick={() => dispatch(setGameState("home"))}>
            Home
          </button>
        </div>
      )}
    </div>
  );
};

export default App;
